package com.bupt.syc.poetry;

public class shiren {
	String name;
	String chaodai;
	String hao;
	String zi;
	String shiciji;
	
	
	public shiren(String name, String chaodai, String zi, String shiciji) {
		super();
		this.name = name;
		this.chaodai = chaodai;
		this.zi = zi;
		this.shiciji = shiciji;
	}
	public shiren() {
		// TODO Auto-generated constructor stub
	}
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	public String getChaodai() {
		return chaodai;
	}
	public void setChaodai(String chaodai) {
		this.chaodai = chaodai;
	}
	public String getZi() {
		return zi;
	}
	public void setZi(String zi) {
		this.zi = zi;
	}
	public String getShiziji() {
		return shiciji;
	}
	public void setShiziji(String shiciji) {
		this.shiciji = shiciji;
	}
	public String getHao() {
		return hao;
	}
	public void setHao(String hao) {
		this.hao = hao;
	}
	
	
	
}
